<script>
    // alert('{{app()->getLocale()}}');
    function hideLanguageInMenu() {
        const currentPath = window.location.pathname;
        const languageMatch = currentPath.match(/^\/(ru|en)\//i);
        
        if (languageMatch) {
            const currentLanguage = languageMatch[1].toLowerCase();
            const menuItems = document.querySelectorAll('.menu-expander-items p');
            const activeLanguage = document.querySelector('.active-language');
            
            menuItems.forEach(item => {
                if (item.textContent.trim().toLowerCase() === currentLanguage) {
                    item.style.display = 'none';
                }
            });

            if (activeLanguage) {
                activeLanguage.textContent = currentLanguage.toUpperCase();
            }
        }
    }
    
    document.addEventListener('DOMContentLoaded', hideLanguageInMenu);
    
    // $.removeCookie("userLanguage", { path: "/" });
    const userLanguage = navigator.language || navigator.userLanguage; userLanguage = userLanguage.slice(0, -3)
    if($.cookie("userLanguage") == undefined) {
        setLanguage(userLanguage); 
    }

    function setLanguage (language) {
        document.cookie = `userLanguage=${language}; expires=Fri, 31 Dec 9999 23:59:59 GMT; path=/`;   
        const currentUrl = new URL(window.location.href);
        if (currentUrl.pathname == '/ru') {
            currentUrl.pathname = currentUrl.pathname.replace(/^\/(ru|en)/, '/');
        }
        else {
            currentUrl.pathname = currentUrl.pathname.replace(/^\/(ru|en)\//, '/');
        }
        // alert(currentUrl.pathname);
        // currentUrl.pathname = currentUrl.pathname.replace(/^\/(ru|en)/, '/');
        if (language != 'en') {
            currentUrl.pathname = '/'+language+currentUrl.pathname;
        }
        else {
            currentUrl.pathname = currentUrl.pathname;
        }
        currentUrl.href = currentUrl.href.replace(/\/$/, '')
        window.location.href = currentUrl.href;
    }
    
</script>